<script>
export default {
    async asyncData(context){
      try{
          const contact = await context.app.$axios.$get(`/api/contact`);
          return {contact}
      }catch(e){
          context.error(e);
      }
    

    },
    data(){
        return{
            errors:[],
            name:'',
            message:'',
            email:'',
            loading:false
        }
    },
    methods:{
        contact(){
            this.loading = true;
            let formData = new FormData();
            formData.append('name',this.name);
            formData.append('message',this.name);
            formData.append('email',this.email);
            this.$axios.post('/api/contact',formData).then(res=>{
            this.message = "";
            this.name = "";
            this.email = "";
            this.loading = false;
            this.$toast.show('Your Message Has Been Sent',{type:'success',duration:3000});
            
            }).catch(err=>{
           
            this.errors = err.response.data.errors;
            this.loading = false;
            this.$toast.show('Authentication Error Occurred',{type:'error',duration:3000});
            })

        }
    }
}
</script>